	public function test_search_paths_includes_theme() {
		$paths = $this->styles->search_paths();
		$found = false;

		foreach ( $paths as $path ) {
			if ( false !== strpos( $path, get_stylesheet_directory() ) ) {
				$found = true;
				break;
			}
		}

		$this->assertTrue( $found );
	}

