			}
		}

		$this->assertTrue( $found );
	}

	public function test_load_variables_normal() {
		$tmp_option_styles = get_option( 'styles' );
		$tmp_option_styles_preview = get_option( 'styles-preview' );

		delete_option( 'styles' );
		delete_option( 'styles_preview' );

		$test_data = 'Normal styles option.';
		update_option( 'styles', $test_data );
		$this->styles->load_variables();
		$this->assertEquals( $test_data, $this->styles->variables );

		$test_data = 'Preview styles option.';
		update_option( 'styles-preview', $test_data );
		$_GET['preview'] = true;
		$this->styles->load_variables();
		$this->assertEquals( $test_data, $this->styles->variables );

		unset( $_GET['preview'] );

		update_option( 'styles', $tmp_option_styles);
		update_option( 'styles-preview', $tmp_option_styles_preview );
	}

	public function test_get_file() {
		$file_paths = $this->styles->get_file();

		$this->assertFalse( empty( $file_paths['uri'] ) );
		$this->assertFalse( empty( $file_paths['path'] ) );
		$this->assertFalse( empty( $file_paths['cache_path'] ) );
		$this->assertFalse( empty( $file_paths['cache_uri'] ) );
	}

	public function test_get_cache_file_single_site() {
		$this->assertEquals( '/styles/cache.css', $this->styles->get_cache_file() );
	}

	
